            i = i if bool(mem.get(p, 0)) else braces[i]
        elif cmd == 'E':
            i = braces[i] if mem.get(p, 0) else i
        elif cmd == 'P':
            output.append(chr(mem.get(p, 0)))
        elif cmd == 'N':
            output.append(str(mem.get(p, 0)))
        elif cmd == '+':
            updateMem(add, 1)
